import React from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  FlatList,
} from "react-native";
import { HorizontalFoodCard } from "../../components";
import { FONTS, COLORS, SIZES, icons, dummyData } from "../../constants";
const Home = () => {
  const [selectedCategoryId, setselectedCategoryId] = React.useState(1);
  const [selectedMenuType, setselectedMenuType] = React.useState(1);
  const [menuList, setmenuList] = React.useState([]);

  React.useEffect(() => {
    handlerChangeCategory(selectedCategoryId, selectedMenuType);
  }, []);

  //handler

  function handlerChangeCategory(categoryId, menuTypeId) {
    //Find The menu based on the menytypeid
    let selectedMenu = dummyData.menu.find((a) => a.id == menuTypeId);

    //set the menu based on the categoryid
    setmenuList(
      selectedMenu?.list.filter((a) => a.categories.includes(categoryId))
    );
  }

  //render

  function renderSearch() {
    return (
      <View
        style={{
          flexDirection: "row",
          height: 40,
          alignItems: "center",
          marginHorizontal: SIZES.padding,
          marginVertical: SIZES.base,
          paddingHorizontal: SIZES.radius,
          borderRadius: SIZES.radius,
          backgroundColor: COLORS.lightGray2,
        }}
      >
        {/* Icon */}
        <Image
          source={icons.search}
          style={{
            height: 20,
            width: 20,
            tintColor: COLORS.black,
          }}
        />

        {/* Text Input*/}
        <TextInput
          style={{
            flex: 1,
            marginLeft: SIZES.radius,
            ...FONTS.body3,
          }}
          placeholder="Search Food ..."
        />

        {/* filter Button*/}
        <TouchableOpacity
        //onpress
        >
          <Image
            source={icons.filter}
            style={{
              height: 20,
              width: 20,
              tintColor: COLORS.black,
            }}
          />
        </TouchableOpacity>
      </View>
    );
  }
  return (
    <View
      style={{
        flex: 1,
      }}
    >
      {/* Search */}
      {renderSearch()}

      {/* List */}
      <FlatList
        data={menuList}
        keyExtractor={(item) => `${item.id}`}
        showsVerticalScrollIndicator={false}
        renderItem={({ item, index }) => {
          return (
            <HorizontalFoodCard
              containerStyle={{
                height: 130,
                alignItems: "center",
                marginHorizontal: SIZES.padding,
                marginBottom: SIZES.radius,
              }}
              imageStyle={{
                marginTop: 20,
                height: 110,
                width: 110,
              }}
              item={item}
              onPress={() => console.log("HorizontalFoodCard ")}
            />
          );
        }}
      />
    </View>
  );
};

export default Home;
